      "createdAt", "updatedAt"
    ) VALUES (
      'resp_cmgbi0rtf000kro1rnxap0iti', '*"We regret the one-sided position taken by Senator Hoffmann that ignores the terrorist attacks against Israeli civilians and fails to recognize Israel''s right to defend itself."*', 'RESPONSE', 'CRITICISM', '2023-10-07T23:00:00.000Z',
      'The statement was covered by Brazilian Jewish media and added to the broader debate about Brazilian political positions on the conflict.', NULL,
      NULL, 'cmgbi0rtd000iro1rn5qazdc6', 'cmgbi0rsw000aro1rvvp3l3sm',
      '2025-10-03T23:52:04.563Z', '2025-10-03T23:52:04.563Z'
