INSERT INTO "Statement" (
      id, content, "statementType", "responseType", "statementDate",
      "responseImpact", "respondsToId",
      "personId", "organizationId", "incidentId",
      "createdAt", "updatedAt"
    ) VALUES (
      'resp_cmgbi2wdg000kroa4v1xp0gcm', '*"Deputy Wyllys'' use of the term ''genocide'' is completely inappropriate and shows a lack of understanding of the complex situation in the Middle East. We reject these inflammatory accusations against Israel''s right to defend itself."*', 'RESPONSE', 'CRITICISM', '2018-05-14T23:00:00.000Z',
      'The statement was circulated among Brazilian Jewish community organizations', NULL,
      NULL, 'cmgbi2wdf000iroa4akl78fm5', 'cmgbi2wd4000aroa4mmenx76k',
      '2025-10-03T23:53:43.780Z', '2025-10-03T23:53:43.780Z'
    );
INSERT INTO "Statement" (
